template<class T> std::ostream& operator<<(std::ostream& os, const std::vector<T>& a) { for (const T& x : a) os << x << ' '; return os; }

class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        std::vector<int> a;
        for (int i = 0, last = 0; i <= n; i++) {
            if (i == n || s[i] == '0') {
                if (i-last > 0) a.push_back(i-last);
                last = i+1;
            }
        }
        // std::cout << "a = " << a << '\n';
        long long ans = 0LL;
        int sum = 0;
        int sz = a.size();
        for (int i = 0; i < sz; i++) {
            ans += sum;
            sum += a[i];
        }
        if (s.back() == '0') ans += sum;
        return ans;
    }
};
