class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int result = 0;
        int n = s.size();
        for (int i = 0; i < n - 1; i++) {
            if ('1' == s[i]) {
                cnt++;
            }
            if ('1' == s[i] && '0' == s[i + 1]) {
                result += cnt;
            }
        }
        return result;
    }
};
