class Solution {
public:
    int maxOperations(string s) {
        int ans = 0,cont = 0,aux = 0;
        for(int i=0;i<s.length();i++){
            if(s[i]=='1') cont++;
            if(s[i]=='0'){
                if(cont>0){
                    aux+=cont;
                    ans+=aux;
                }
                cont = 0;
            }
        }
        return ans;
    }
};
