class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        left = 0
        n = len(s)
        i = 0
        while i < n:
            while i < n and s[i] == "0":
                i += 1
                left += 1
            if i > 0 and s[i-1] == '0':
                ans += i - left
            i += 1
        return ans
