class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int cnt=0;
        int n=s.size();
        for(int i=0;i<n;){
            if(s[i]=='0'){
                ans+=cnt;
                while(i<n&&s[i]=='0')
                    ++i;
            }
            while(i<n&&s[i]=='1'){
                ++i;
                ++cnt;
            }
        }
        return ans;
    }
};
