class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int pre=-1;
        vector<int> dis;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1'&&pre!=-1)
            {
                dis.push_back(i-pre-1);
            }
            if(s[i]=='1')
            pre=i;
        }
        if(pre!=-1)
        dis.push_back(n-1-pre);
        int t=dis.size()-1;
        if(dis.size()==0)
        return 0;
        while(t>=0&&dis[t]==0)
        {
            t--;
        }
        int cnt=0;
        int ans=0; 
        for(int i=0;i<=t;i++)
        {
            if(dis[i]==0)
            {
                cnt++;
            }
        }
        for(int i=0;i<=t;i++)
        {
            ans+=t-i+1-cnt;
            if(dis[i]==0)
            cnt--;
        }
        return ans;
    }
};
