class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        int flag = 0;
        for (auto i : s) {
            if (i == '1') {
                flag = 1;
                cnt++;
            }
            if (i == '0') {
                if (flag != 0) {
                    flag = 0;
                    ans += cnt;
                }
            }
        }
        return ans;
    }
};
