class Solution {
public:
    int maxOperations(string s) {
        int res=0;
        s.push_back('1');

        for (int i=0, k=0, p=0; i<s.size(); ++i) {
            if (s[i]=='1') {
                if (p) res+=k;
                ++k;
                p=0;
            } else {
                p=1;
            }
        }

        return res;
    }
};
