class Solution {
public:
    int maxOperations(string s) {
        int result=0,count=0;
        for(int i=s.length()-1;i>=0;)
            if(s[i]=='0') {
                while(i>=0&&s[i]=='0')
                    i--;
                count++;
            } else
                result+=count,i--;
        return result;
    }
};
