class Solution {
public:
    int solve(string &s){
         vector<int> segments;
    int length = 0;
    for (char c : s) {
        if (c == '0') {
            if (length > 0) {
                segments.push_back(length);
            }
            length = 0;
        } else {
            length++;
        }
    }
    segments.push_back(length);

    int totalOperations = 0;
    int accumulatedSum = 0;
    for (int segment : segments) {
        totalOperations += accumulatedSum;
        accumulatedSum += segment;
    }
    return totalOperations;
    }
    int maxOperations(string s) {
       return solve(s);
    }
};
