#include <bits/stdc++.h>

using namespace std;

using ll = long long;
using ld = long double;
using i128 = __int128_t;



class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int ans = 0;
        int N = s.size();
        for (int i = 0; i < N; ) {
            if (s[i] == '1') {
                int j = i;
                int cur = 0;
                while (j < N && s[j] == '1') {
                    j++;
                    cur++;
                }
                ans += cnt;
                cnt += cur;
                i = j;
            } else i++;
        }
        if(s.back()=='0'){
            ans+=cnt;
        }
        return ans;
    }
};
