class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        count = 0
        n = len(s)
        i = 0
        while i < n-1:
            if s[i] == '1' and s[i+1] == '0':
                count+=1
                res+=count
                i+=1
                while i < n-1 and s[i] == '0':
                    i+=1
            else:
                if s[i] == '1':
                    count+=1
                i+=1
        return res
