class Solution:
    def maxOperations(self, s: str) -> int:
        sol = 0

        grouping = 0

        add = False
        r = len(s)

        for idx in range(r):
            if s[idx] == "1":

                if add:
                    add = False
                    sol += grouping

                if idx != r - 1 and s[idx+1] == "0":
                    add = True

                grouping += 1

        sol += grouping if s[-1] == "0" else 0
        return sol






