class Solution {
public:
    int maxOperations(string s) {
        int countOnes = 0;
        int res = 0;
        bool flag = false;
        for (int i = 0, n = s.size(); i < n; ++i) {
            if (s[i] - '0' == 1) {
                ++countOnes;
                flag = true;
            } else if (flag) {
                res += countOnes;   
                flag = false;
            }
        }
        return res;
    }
};
