class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        // int i=s.length()-1;
        int j = s.length()-1;
        while(j<s.length() && s[j] == '1') {
            j--;
        }
        int prev = 0;
        int noOne = 0;
        for(int i=0;i<=j;) {
            if(s[i] == '1') {
                cnt++;
                i++;
                noOne++;
            }
            else {
                cnt += prev;
                prev = noOne;
                while(i<s.length() && s[i] == '0') {
                    i++;
                }
            }
        }
        return cnt;
    }
};
