class Solution {
public:
    int maxOperations(string s) {
        int count =0, ans = 0;
        bool sub = false;
        for(int i=0; i<s.length(); i++){
            if(s[i] == '0'){
                if(count)   sub = true;
            }
            else{
                if(sub) ans += count; 
                count += 1;
                sub = false;
            }
        }
        if(sub) ans += count;
        return ans;
    }
};
