class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int ones = 0;
        int res = 0;
        bool added = false;
        for (int i=0; i<n; ++i) {
            if (s[i] == '0' && !added) {
                res += ones;
                added = true;
            } else if (s[i] == '1') {
                ++ones;
                added = false;
            }
        }
        return res;
    }
};
