class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.length();
        int curr = 0;
        int j = n-1;
        while(j>=0){
            if(s[j]=='0'){
                curr++;
                while(j>=0 && s[j]=='0'){
                    // curr++;
                    j--;
                }
            }
            else{
                ans+=curr;
                j--;
            }
        }
        return ans;

    }
};
