class Solution {
    public int maxOperations(String s) {
        var n = s.length();
        var count = 0;

        var prev = 0;
        for(var i=n-2; i>=0; i--){
            if(s.charAt(i) == '1'){
                var curr = prev;
                if(s.charAt(i+1) == '0') curr++;
                count += curr;
                prev = curr;
            }
        }

        return count;
    }
}
