class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        odd = 0
        zero = False
        for ch in s:
            if ch == '1':
                if zero:
                    ans += odd
                    zero = False
                odd += 1
            else:
                zero = True
        if zero:
            ans += odd
        return ans
