class Solution {
public:
    int maxOperations(string s) {
       int res =0 ; 
       int cur =0  ; 
       bool  f = true ;  
       for (int  i =0 ; i<s.size() ; i++){
            if(s[i] == '1') {
                cur ++  ;  
                f = true ;  
            }else {
                if(f) {
                    res+=cur ;  
                    f= false  ;  
                }
            }

       }
       return res  ;  

    }
};
