class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int sum = 0;
        int tmp = 0;
        int n = s.size();
        for (int i = 0; i < n; i ++) {
            if (s[i] == '1') tmp ++;
            if (tmp && (i == n - 1 || s[i + 1] == '0')) {
                ans += sum;
                sum += tmp;
                tmp = 0;
            }

        }
        if (s[n - 1] == '0') ans += sum;
        return ans;
    }
};
