class Solution {
    public int maxOperations(String s) {
        char[] chs = s.toCharArray();
        int ans = 0;
        int count = 0;
        for(int i = 0; i < chs.length; ){
            if(chs[i] == '1'){
                count++;
                i++;
            } else{
                ans += count;
                i++;
                while(i < chs.length && chs[i] == '0') i++;
            }
        }
        return ans;
    }
}
