class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        if (n == 1) return 0;
        char[] arr = s.toCharArray();
        int ans = 0, zeros = 0, i = n-1;
        while (i >= 0){
            if (arr[i] == '0'){
                zeros++;
                while (i >= 0 && arr[i] == '0'){
                    i--;
                }
            }
            else{
                ans += zeros;
                i--;
            }
        }
        return ans;
    }
}
