class Solution {
public:
    int maxOperations(string s){
        int ans=0;
        int n=s.size();
        int cnt=0;
        bool prev_zero=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                cnt++;
                prev_zero=0;
            }
            else{
                if(!prev_zero){
                    ans+=cnt;
                }
                prev_zero=1;
            }
        }
        return ans;
    }
};
