class Solution {
  public:
    int maxOperations(string s) {
        int n = s.size();
        int curr1 = 0, ans = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                ++curr1;
            } else {
                if (i < n - 1 && s[i + 1] == '0') {
                    continue;
                }
                ans += curr1;
            }
        }

        return ans;
    }
};
