class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        long long res = 0;
        int cur = 0;
        for (int i = 0; i < n; ++i)
        {
            if (s[i] == '1')
            {
                cur += 1;
                continue;
            }
            res += cur;
            while (i + 1 < n && s[i + 1] == '0') i += 1;
        }
        return res;
    }
};
