class Solution {
    public int maxOperations(String s) {
        if (s.isEmpty()) return 0;
        char[] sarr = s.toCharArray();
        int result = 0, zeroG = 0;
        for (int i = s.length() - 1; i >= 0; i--) {
            if (sarr[i] == '0') {
                if (i == s.length() - 1 || sarr[i + 1] == '1') {
                    zeroG++;
                }
            } else {
                result += zeroG;
            }
        }

        return result;
    }
}
