class Solution {
public:
    int maxOperations(string s) {
        int ct = 0; 
        int ans = 0;
        bool flag = 1;
        for(int i=0;i<s.size();i++){
            if(s[i]=='0'){
                if(flag){
                 ans+=ct;
                 flag=0;
                }
            }
            else{
                ct++;
                flag=1;
            }
        }
        return ans;
    }
};
