class Solution {
    public int maxOperations(String s) {
        int ans =0;
        int c1=0;
        for (int i = 0; i < s.length()-1; i++) {
            if(s.charAt(i)=='1'){
                c1++;
            }
            if(s.charAt(i)=='1'&&s.charAt(i+1)=='0'){
                ans+=c1;
            }
        }
        return ans;
    }
}
