class Solution {
    public int maxOperations(String s) {
        char arr[] = s.toCharArray();

        int oneLeft = 0;
        int leftIndex = 0;
        int ans = 0;

        for(int i = 0; i < arr.length; i++) {
            if(arr[i] == '1') {
                if(oneLeft > 0 && (i - leftIndex - 1) > 0)
                    ans += oneLeft;

                oneLeft++;
                leftIndex = i;
            }
        }

        if(oneLeft > 0 && (arr.length - leftIndex - 1) > 0)
            ans += oneLeft;

        return ans;
    }
}
