#define f first
#define s second
#define pb push_back
#define all(x) x.begin(),x.end()
#define sz(x) (long long)(x).size()
#define pii pair<long long,long long>

class Solution {
public:
    int maxOperations(string s) {
        int n=sz(s);
        int ans=0,cnt1=0;
        for(int i=0;i<n-1;i++){
            if(s[i]=='1' && s[i+1]=='0'){
                cnt1++;
                ans+=cnt1;
            }
            else if(s[i]=='1'){
                cnt1++;
            }
        }
        return ans;
    }
};
