class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int ans = 0;
        if(s[0] == '1'){
            cnt++;
        }
        if(s.size() == 1){
            return 0;
        }
        for(int i=1;i<s.size();i++){
            if(s[i]=='1'  && s[i-1] == '0'){
                ans += cnt;
            }
            if(s[i] == '1'){
                cnt++;
            }
        }
        int n = s.size();
        if(s[n-1] == '0') ans += cnt;
        return ans;
    }
};
