class Solution {
    public int maxOperations(String s) {
        int res = 0;
        char[] cs = s.toCharArray();
        int count = 0; // 1的个数
        boolean pre = false; // 前面是否有空格
        for (int i = 0; i < cs.length; i++) {
            if (cs[i] == '1'){
                if (pre) {
                    res += count;
                }
                count++;
                pre = false;
            }else{
                pre = true;
                if (i == cs.length - 1){
                    res += count;
                }
            }
        }
        return res;
    }
}
