class Solution {
public:
    int maxOperations(string s) {
        ll ret = 0;
        ll nrn = 0;
        for (int i=sz(s)-1;i>=0;--i) {
            if (s[i] == '0') {
                if (i+1 >= sz(s) || s[i+1] == '1') {
                    ++nrn;
                }
            }
            if (s[i] == '1') {
                ret += nrn;
            }
        }
        return ret;
    }
};
