class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, n = s.size();
        int one_sum = 0;
        for(int i = 0; i < n;){
            while(i < n && s[i] == '1'){
                one_sum++;
                i++;
            }
            if(i == n) break;
            while(i < n && s[i] == '0') i++;
            ans += one_sum;
        }
        return ans;
    }
};
