class Solution {
    fun maxOperations(s: String): Int {
        var one =true
        var onesLeft = s.count { it=='1' }
        var count = 0
        for (i in s.length-1 downTo 0) {
            if (s[i]=='0' && one) {
                one=false
                count+=onesLeft
            }
            if (s[i]=='1'){
                one=true
                onesLeft--
            }
        }
        return count
    }
}
