class Solution {
public:
    int maxOperations(string s) {
        int c = 0, x = 0, n = s.size();
        bool f = 1;
        for(int i = n-1; i >= 0; i--){
            if(s[i] == '1'){
                f = 1;
                x += c;
            }
            else if(s[i] == '0' && f){
                f = 0;
                c++;
            }
        }
        return x;
    }
};
