class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), zero = 0, ones = 0, total = 0, z_blocks = 0;
        for(int i = n - 1; i >= 0; i--) {
            if(s[i] == '1') {
                ones++;
                if(zero) {
                    zero = 0;
                    z_blocks++;
                }
            }
            else {
                zero++;
                if(ones) {
                    total += ones * z_blocks;
                    ones = 0;
                }
            }
        }
        if(ones)
            total += ones * z_blocks;

        return total;
    }
};
