class Solution {
public:
    int maxOperations(string s) {
        int mul = 0;
        bool prev = false;
        int ans = 0;
        for(int i = s.size() - 1; i>=0; i--){
            if(s[i] == '0'){
                if(prev) continue;
                else{
                    mul += 1;
                    prev = true;
                }
            }else{
                ans += mul;
                prev = false;
            }
        }
        return ans;
    }
};
