class Solution {
public:
    int maxOperations(string s) {
        int n=s.size(),ans=0,cnt=0;
        for(int i=n-1;i>=0;i--) {
            if(s[i]=='0') {
                cnt++;
                while(i>=0 && s[i]=='0') {
                    i--;
                }
                i++;
            }
            else {
                ans+=cnt;
            }
        }
        return ans;
    }
};
