class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int ans = 0;
        bool f = false;
        for(int i = 0;i < s.length();i++)
        {
            if(s[i] == '1')
            {
                f = true;
                cnt++;
            }
            if(s[i] == '0' && f)
            {
                ans += cnt;
                f = false;
            }
        }
        return ans;
    }
};
