class Solution {
public:
    int maxOperations(string s) {
        long long res=0;
        int cnt=0;
        for(int i=s.size()-1;i>=0;--i){
            if(s[i]=='0'){
                if(i+1==s.size() || (s[i+1]=='1')){
                    ++cnt;
                }
            }else{
                res+=cnt;
            }
        }
        return res;
    }
};
