class Solution {
public:
    int maxOperations(string s) {
        s = "0"+ s+"1";
        int l1=0,l2=0,ans=0;
        for(int i = s.size() - 2;i>=0;i--) {
            if(s[i]=='1')  {
                if(s[i+1]=='1') l1++;
                else {
                    l1=1;
                }
            }
            else {
                if(s[i+1]=='1') {
                    ans+=l1*l2;
                    l2++;
                }
            }
        }

        return ans;
    }
};
