class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int x=0;
        long long ans=0;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1') x++;
            else
            {
                ans+=x;
                while(i<n && s[i]=='0')
                {
                    i++;
                }
                i--;
            }

        }
        return ans;
    }
};
