class Solution {
public:
    int maxOperations(string s) {
       int ans = 0;
       int p = s.size();
        int cnt = 0;
       for(int i = 0; i < p; i++){
            if(s[i]=='1'){
                cnt++;
            }else if(i>0 && s[i-1]=='1'){
                ans+=cnt;
            }
       } 
       return ans;
    }
};
