class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int l = s.size() - 2,r = s.size();
        l = 0;
        int p = 0,res = 0;
        while(l < s.size()){
            int k = 0;
            if(s[l] == '0'){l++;continue;}
            res += p;
            while(l < s.size() && s[l] =='1')l++,k++;
            p += k;
        }
        if(s[r - 1] == '0')res += p;
        return res;
    }
};
