typedef long long ll;
class Solution {
public:
    int maxOperations(string s) {
        vector<int>v;
        int cnt = 0;
        for (auto i : s) {
            if (i == '0') {
                if (cnt) v.push_back(cnt);
                cnt = 0;
            }
            else cnt++;
        }
        //for (auto i : v) {cout << i << ",";} cout << endl;
        ll ans = 0 ;
        //reverse(v.begin(), v.end());
        for (int i = 0; i < v.size(); i++) {
            ans += v[i];
            if (i < v.size() - 1) v[i+1] += v[i];
        }
        return ans;
    }
};
