class Solution {
    public int maxOperations(String s) {
        Queue<Integer> q = new LinkedList<>();

        int currBlock = 0;
        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            if (c == '1') {
                currBlock++;
            } else {
                if (currBlock > 0) {
                    q.add(currBlock);
                    currBlock = 0;
                }
            }
        }

        int ops = 0;
        int sum = 0;
        for (int i : q) {
            sum += i;
            ops += sum;
        }
        return ops;
    }
}
