class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();

        int pre = 1, cnt = 0, ret = 0;
        for(int i = n - 1; i >= 0; i--) {
            if(s[i] == '0') {
                if(pre == 1) {
                    cnt++;
                    pre = 0;
                } 
            } else {
                ret += cnt;
                pre = 1;
            }
        }
        return ret;
    }
};
